mapper.select:
select ${distinct}
${resultColumn}
PRODUCT_ID, PRODUCT_NO, PRODUCT_NAME, PRODUCT_CATEGORY, PRODUCT_PROPERTY, DNP_PRICE, MSRP_PRICE, ( CASE WHEN RELEASE_STATUS = 54011001 THEN '未发布' WHEN RELEASE_STATUS = 54011002 THEN '已发布' WHEN RELEASE_STATUS = 54011003 THEN '已取消' ELSE '' END ) RELEASE_STATUS, DATE_FORMAT(RELEASE_DATE,'%Y-%m-%d %H:%m:%s') RELEASE_DATE, DATE_FORMAT(STOP_DATE,'%Y-%m-%d %H:%m:%s') STOP_DATE, (CASE WHEN IS_VALID = 12781001 THEN '是' WHEN IS_VALID = 12781002 THEN '否' ELSE '' END) IS_VALID, (CASE WHEN IS_C_SALE = 12781001 THEN '是' WHEN IS_C_SALE = 12781002 THEN '否' ELSE '' END) IS_C_SALE, (CASE WHEN PRODUCT_DATE = 12 THEN '12个月' WHEN PRODUCT_DATE = 24 THEN '24个月' WHEN PRODUCT_DATE = 36 THEN '36个月' ELSE '' END ) PRODUCT_DATE, PRODUCT_MODEL, DATE_FORMAT(SALES_DATE,'%Y-%m-%d %H:%m:%s') SALES_DATE
from TI_OPI_EXTENDED_DCS
${order}
inner join ( SELECT PRODUCT_ID as primary_key FROM TI_OPI_EXTENDED_DCS
${order}
LIMIT #{startRow},#{rowSize} LIMIT #{rowSize}
) as tmp on tmp.primary_key=TI_OPI_EXTENDED_DCS.PRODUCT_ID

select PRODUCT_ID, PRODUCT_NO, PRODUCT_NAME, PRODUCT_CATEGORY, PRODUCT_PROPERTY, DNP_PRICE, MSRP_PRICE, ( CASE WHEN RELEASE_STATUS = 54011001 THEN '未发布' WHEN RELEASE_STATUS = 54011002 THEN '已发布' WHEN RELEASE_STATUS = 54011003 THEN '已取消' ELSE '' END ) RELEASE_STATUS, DATE_FORMAT(RELEASE_DATE,'%Y-%m-%d %H:%m:%s') RELEASE_DATE, DATE_FORMAT(STOP_DATE,'%Y-%m-%d %H:%m:%s') STOP_DATE, (CASE WHEN IS_VALID = 12781001 THEN '是' WHEN IS_VALID = 12781002 THEN '否' ELSE '' END) IS_VALID, (CASE WHEN IS_C_SALE = 12781001 THEN '是' WHEN IS_C_SALE = 12781002 THEN '否' ELSE '' END) IS_C_SALE, (CASE WHEN PRODUCT_DATE = 12 THEN '12个月' WHEN PRODUCT_DATE = 24 THEN '24个月' WHEN PRODUCT_DATE = 36 THEN '36个月' ELSE '' END ) PRODUCT_DATE, PRODUCT_MODEL, DATE_FORMAT(SALES_DATE,'%Y-%m-%d %H:%m:%s') SALES_DATE from TI_OPI_EXTENDED_DCS
and PRODUCT_NO LIKE concat(concat('%',#{PRODUCT_NO}),'%') and PRODUCT_NAME LIKE concat(concat('%',#{PRODUCT_NAME}),'%') and PRODUCT_CATEGORY LIKE concat(concat('%',#{PRODUCT_CATEGORY}),'%') and PRODUCT_PROPERTY LIKE concat(concat('%',#{PRODUCT_PROPERTY}),'%') and IS_VALID = #{IS_VALID} and PRODUCT_DATE = #{PRODUCT_DATE} and RELEASE_STATUS = #{RELEASE_STATUS} and PRODUCT_MODEL = #{PRODUCT_MODEL} and IS_C_SALE = #{IS_C_SALE} and RELEASE_DATE between #{releaseStart} and #{releaseEnd}
and SALES_DATE between #{salesStart} and #{salesEnd}

select ${distinct}
${resultColumn}
DEALER_CODE, PART_NO, PART_NAME, ( CASE WHEN PART_GROUP_CODE = 11361009 THEN '常规零部件' WHEN PART_GROUP_CODE = 11361010 THEN '第三方' WHEN PART_GROUP_CODE = 11361005 THEN '养护品' WHEN PART_GROUP_CODE = 11361011 THEN '电瓶' WHEN PART_GROUP_CODE = 11361007 THEN '用品' WHEN PART_GROUP_CODE = 11361008 THEN '底盘号相关' ELSE '' END ) PART_GROUP_CODE, ( CASE WHEN PART_PROPERTY = 80021001 THEN 'A' WHEN PART_PROPERTY = 80021001 THEN 'B' WHEN PART_PROPERTY = 80021003 THEN 'C' ELSE '' END ) PART_PROPERTY, CLAIM_PRICE, OEM_LIMIT_PRICE, INSTRUCT_PRICE, LIMIT_PRICE, ( CASE WHEN IS_SJV = 12781001 THEN '是' ELSE '否' END ) IS_SJV, ( CASE WHEN IS_MOP = 12781001 THEN '是' ELSE '否' END ) IS_MOP, ( CASE WHEN IS_C_SALE = 12781001 THEN '是' ELSE '否' END ) IS_C_SALE, ( CASE WHEN PART_STATUS = 10011001 THEN '否' ELSE '是' END ) PART_STATUS
from TM_PART_INFO
${order}
inner join ( SELECT PART_NO as primary_key FROM TM_PART_INFO
${order}
LIMIT #{startRow},#{rowSize} LIMIT #{rowSize}
) as tmp on tmp.primary_key=TM_PART_INFO.PART_NO
LIMIT #{startRow},#{rowSize}

		select
		${distinct}

			${resultColumn}

				*


		from TI_OPI_EXTENDED_MODEL 





                ${order}


                inner join
                (
                SELECT PRODUCT_MODEL_ID as primary_key FROM TI_OPI_EXTENDED_MODEL



                ${order}

                    LIMIT #{startRow},#{rowSize} 
                    LIMIT #{rowSize}

                 ) as tmp
                 on tmp.primary_key=TI_OPI_EXTENDED_MODEL.PRODUCT_MODEL_ID


	    LIMIT #{startRow},#{rowSize}
