mapper.select:
select ${distinct}
${resultColumn}
PRODUCT_ID, PRODUCT_NO, PRODUCT_NAME, (CASE WHEN PRODUCT_CATEGORY = 81021001 THEN '延保' ELSE '道路救援' END) PRODUCT_CATEGORY, PRODUCT_PROPERTY, DNP_PRICE, MSRP_PRICE, ( CASE WHEN RELEASE_STATUS = 54011001 THEN '未发布' WHEN RELEASE_STATUS = 54011002 THEN '已发布' WHEN RELEASE_STATUS = 54011003 THEN '已取消' ELSE '' END ) RELEASE_STATUS, DATE_FORMAT(RELEASE_DATE,'%Y-%m-%d') RELEASE_DATE, DATE_FORMAT(STOP_DATE,'%Y-%m-%d') STOP_DATE, (CASE WHEN IS_VALID = 12781001 THEN '是' WHEN IS_VALID = 12781002 THEN '否' ELSE '' END) IS_VALID, (CASE WHEN IS_C_SALE = 12781001 THEN '是' WHEN IS_C_SALE = 12781002 THEN '否' ELSE '' END) IS_C_SALE, (CASE WHEN PRODUCT_DATE = 12 THEN '12个月' WHEN PRODUCT_DATE = 24 THEN '24个月' WHEN PRODUCT_DATE = 36 THEN '36个月' ELSE '' END ) PRODUCT_DATE, PRODUCT_MODEL, DATE_FORMAT(SALES_DATE_START,'%Y-%m-%d %H:%m:%s') SALES_DATE_START, DATE_FORMAT(SALES_DATE_END,'%Y-%m-%d %H:%m:%s') SALES_DATE_END, ( CASE WHEN 1 = 1 THEN concat( DATE_FORMAT( SALES_DATE_START, '%Y-%m-%d' ), '--', DATE_FORMAT(SALES_DATE_END, '%Y-%m-%d') ) ELSE '' END ) SALES_DATE_ALL
from TI_OPI_EXTENDED_DCS
LIMIT #{startRow},#{rowSize}

select PRODUCT_ID, PRODUCT_NO, PRODUCT_NAME, (CASE WHEN PRODUCT_CATEGORY = 81021001 THEN '延保' ELSE '道路救援' END) PRODUCT_CATEGORY, PRODUCT_PROPERTY, DNP_PRICE, MSRP_PRICE, ( CASE WHEN RELEASE_STATUS = 54011001 THEN '未发布' WHEN RELEASE_STATUS = 54011002 THEN '已发布' WHEN RELEASE_STATUS = 54011003 THEN '已取消' ELSE '' END ) RELEASE_STATUS, DATE_FORMAT(RELEASE_DATE,'%Y-%m-%d') RELEASE_DATE, DATE_FORMAT(STOP_DATE,'%Y-%m-%d') STOP_DATE, (CASE WHEN IS_VALID = 12781001 THEN '是' WHEN IS_VALID = 12781002 THEN '否' ELSE '' END) IS_VALID, (CASE WHEN IS_C_SALE = 12781001 THEN '是' WHEN IS_C_SALE = 12781002 THEN '否' ELSE '' END) IS_C_SALE, (CASE WHEN PRODUCT_DATE = 12 THEN '12个月' WHEN PRODUCT_DATE = 24 THEN '24个月' WHEN PRODUCT_DATE = 36 THEN '36个月' ELSE '' END ) PRODUCT_DATE, PRODUCT_MODEL, DATE_FORMAT(SALES_DATE_START,'%Y-%m-%d %H:%m:%s') SALES_DATE_START, DATE_FORMAT(SALES_DATE_END,'%Y-%m-%d %H:%m:%s') SALES_DATE_END, ( CASE WHEN 1 = 1 THEN concat( DATE_FORMAT( SALES_DATE_START, '%Y-%m-%d' ), '--', DATE_FORMAT(SALES_DATE_END, '%Y-%m-%d') ) ELSE '' END ) SALES_DATE_ALL from TI_OPI_EXTENDED_DCS
and PRODUCT_NO LIKE concat(concat('%',#{PRODUCT_NO}),'%') and PRODUCT_NAME LIKE concat(concat('%',#{PRODUCT_NAME}),'%') and PRODUCT_CATEGORY = #{PRODUCT_CATEGORY} and PRODUCT_PROPERTY LIKE concat(concat('%',#{PRODUCT_PROPERTY}),'%') and IS_VALID = #{IS_VALID} and PRODUCT_DATE = #{PRODUCT_DATE} and RELEASE_STATUS = #{RELEASE_STATUS} and PRODUCT_MODEL = #{PRODUCT_MODEL} and IS_C_SALE = #{IS_C_SALE} and RELEASE_DATE between #{releaseStart} and #{releaseEnd}
and SALES_DATE_START >= #{salesStart}
and SALES_DATE_END <= #{salesEnd}

select ${distinct}
${resultColumn}
DEALER_CODE, PART_NO, PART_NAME, ( CASE WHEN PART_GROUP_CODE = 11361009 THEN '常规零部件' WHEN PART_GROUP_CODE = 11361010 THEN '第三方' WHEN PART_GROUP_CODE = 11361005 THEN '养护品' WHEN PART_GROUP_CODE = 11361011 THEN '电瓶' WHEN PART_GROUP_CODE = 11361007 THEN '用品' WHEN PART_GROUP_CODE = 11361008 THEN '底盘号相关' ELSE '' END ) PART_GROUP_CODE, ( CASE WHEN PART_PROPERTY = 80021001 THEN 'A' WHEN PART_PROPERTY = 80021001 THEN 'B' WHEN PART_PROPERTY = 80021003 THEN 'C' ELSE '' END ) PART_PROPERTY, CLAIM_PRICE, OEM_LIMIT_PRICE, INSTRUCT_PRICE, LIMIT_PRICE, ( CASE WHEN IS_SJV = 12781001 THEN '是' ELSE '否' END ) IS_SJV, ( CASE WHEN IS_MOP = 12781001 THEN '是' ELSE '否' END ) IS_MOP, ( CASE WHEN IS_C_SALE = 12781001 THEN '是' ELSE '否' END ) IS_C_SALE, ( CASE WHEN PART_STATUS = 10011001 THEN '否' ELSE '是' END ) PART_STATUS
from TM_PART_INFO
GROUP BY PART_NO,PART_NAME LIMIT #{startRow},#{rowSize}

select PRODUCT_ID from TI_OPI_EXTENDED_DCS

	 and PRODUCT_NO LIKE
		concat(concat('%',#{PART_NO}),'%')

	 and PRODUCT_NAME LIKE
		concat(concat('%',#{PART_NAME}),'%')
	 and PRODUCT_CATEGORY = #{PART_GROUP_CODE} 
	 and PRODUCT_PROPERTY LIKE
		concat(concat('%',#{PART_PROPERTY}),'%')
	 and DNP_PRICE = #{CLAIM_PRICE}
	 and MSRP_PRICE = #{LIMIT_PRICE}
	 and IS_C_SALE = #{IS_C_SALE_INT}

select count(*) from ti_opi_extended_model

	 and PRODUCT_ID = #{PRODUCT_ID}
	 and BRAND_ID = #{BRAND_ID}
	 and BRAND_CODE = #{BRAND_CODE}
	 and BRAND_NAME = #{BRAND_NAME}
	 and SERIES_ID = #{SERIES_ID}
	 and SERIES_CODE = #{SERIES_CODE}
	 and SERIES_NAME = #{SERIES_NAME}
	 and MODEL_ID = #{MODEL_ID}
	 and MODEL_CODE = #{MODEL_CODE}
	 and MODEL_NAME = #{MODEL_NAME}

select count(*) from ti_opi_extended_model

	 and PRODUCT_ID = #{PRODUCT_ID}
	 and BRAND_ID = #{BRAND_ID}
	 and BRAND_CODE = #{BRAND_CODE}
	 and BRAND_NAME = #{BRAND_NAME}
	 and SERIES_ID = #{SERIES_ID}
	 and SERIES_CODE = #{SERIES_CODE}
	 and SERIES_NAME = #{SERIES_NAME}
