mapper.select:
select * from (SELECT ttes.PRODUCT_SALES_ID, ttes.DEALER_CODE AS DEALER_CODE_TTES, TEDC.DEALER_CODE AS DEALER_CODE_TEDC, tmd.DEALER_CODE, tmd.DEALER_NAME, tmd.DEALER_SHORTNAME, tmd.COMPANY_ID, tmd.DEALER_ID, tmd.MARKETING, tmd.STATUS, tmd.DEALER_LEVEL, tmd.ADDRESS, tmd.DEALER_TYPE, ttes.PRODUCT_ID, ORG2.ORG_NAME AS BIG_ORG, ORG2.ORG_ID AS BIG_ORG_ID, ORG1.ORG_NAME AS SMALL_ORG, ORG1.ORG_ID AS SMALL_ORG_ID, ( CASE WHEN ttes.ORDER_STATUS = 55011001 THEN '已保存' WHEN ttes.ORDER_STATUS = 55011002 THEN '已结案' WHEN ttes.ORDER_STATUS = 55011003 THEN '扣款失败' WHEN ttes.ORDER_STATUS = 55011004 THEN '已作废' ELSE '' END ) ORDER_STATUS, ttes.PRODUCT_SALES_ORDER, ttes.SAP_SALES_ORDER, ttes.PRODUCT_NO, ttes.PRODUCT_NAME, toed.PRODUCT_DESCRIBTION, ( CASE WHEN toed.PRODUCT_DATE = 12 THEN '12个月' WHEN toed.PRODUCT_DATE = 24 THEN '24个月' WHEN toed.PRODUCT_DATE = 36 THEN '36个月' ELSE '' END ) PRODUCT_DATE, ttes.CUSTOMER_NAME, ttes.CUSTOMER_CONTACT, ttes.VIN, (CASE WHEN 1=1 THEN DATE_FORMAT(ttes.BILLING_AT,'%Y-%m-%d %H:%m:%s') ELSE '' END ) BILLING_AT, ttes.LICENSE_NO, ttes.OWNER_NO, ttes.OWNER_NAME, ttes.OWNER_PHONE, ttes.OWNER_MOBILE, ttes.BRAND_NAME, ttes.BRAND_CODE, ttes.SERIES_NAME, ttes.SERIES_CODE, ttes.MODEL_NAME, ttes.MODEL_CODE, ttes.APACKAGE, ttes.YEAR_MODEL, (CASE WHEN 1=1 THEN DATE_FORMAT(ttes.TAKE_EFFECT_START,'%Y-%m-%d %H:%m:%s') ELSE '' END ) TAKE_EFFECT_START, (CASE WHEN 1=1 THEN DATE_FORMAT(ttes.TAKE_EFFECT_END,'%Y-%m-%d %H:%m:%s') ELSE '' END ) TAKE_EFFECT_END, ctu.USER_NAME CREATE_NAME, (CASE WHEN 1=1 THEN DATE_FORMAT(ttes.CREATED_AT,'%Y-%m-%d %H:%m:%s') ELSE '' END ) CREATED_AT, ttes.PURCHASE_NUMBER, ttes.TERMINAL_NON_SALES_PRICE, ttes.TERMINAL_SALES_PRICE, ttes.ACTUAL_NON_SALES_PRICE, ttes.TOTAL_AMOUNT, ltu.USER_NAME CLOSED_NAME, (CASE WHEN 1=1 THEN DATE_FORMAT(ttes.CLOSED_AT,'%Y-%m-%d %H:%m:%s') ELSE '' END ) CLOSED_AT, itu.USER_NAME INVALID_NAME, (CASE WHEN 1=1 THEN DATE_FORMAT(ttes.INVALID_AT,'%Y-%m-%d %H:%m:%s') ELSE '' END ) INVALID_AT FROM tt_opi_extended_sales ttes LEFT JOIN ti_opi_extended_dcs toed ON ttes.PRODUCT_ID = toed.PRODUCT_ID LEFT JOIN tm_user ctu ON ttes.CREATED_BY = ctu.USER_ID LEFT JOIN tm_user ltu ON ttes.CLOSED_BY = ltu.USER_ID LEFT JOIN tm_user itu ON ttes.INVALID_BY = itu.USER_ID LEFT JOIN TM_ENTITY_DEALER_CHANGE TEDC ON ttes.DEALER_CODE = TEDC.ENTITY_CODE LEFT JOIN tm_dealer tmd ON TEDC.DEALER_CODE = REPLACE (tmd.DEALER_CODE, 'A', '') LEFT JOIN tm_dealer_org_relation tdor ON tdor.DEALER_ID = tmd.DEALER_ID LEFT JOIN tm_org ORG1 ON ORG1.ORG_ID = tdor.ORG_ID LEFT JOIN tm_org ORG2 ON ORG2.ORG_ID = ORG1.PARENT_ORG_ID) bsm where 1=1 and bsm.ORDER_STATUS= 55011002 AND bsm.BIG_ORG_ID=#{BIG_ORG_ID} AND bsm.SMALL_ORG_ID=#{SMALL_ORG_ID}
AND bsm.DEALER_CODE in
#{item}
AND bsm.DEALER_NAME LIKE CONCAT('%',#{DEALER_NAME},'%')
AND bsm.PRODUCT_DATE =#{PRODUCT_DATE} AND bsm.PRODUCT_SALES_ORDER LIKE CONCAT('%',#{PRODUCT_SALES_ORDER},'%') AND bsm.PRODUCT_NO LIKE CONCAT('%',#{PRODUCT_NO},'%') AND bsm.PRODUCT_NAME LIKE CONCAT('%',#{PRODUCT_NAME},'%')
AND bsm.BRAND_CODE =#{BRAND_CODE} AND bsm.SERIES_CODE =#{SERIES_CODE} AND bsm.MODEL_CODE =#{MODEL_CODE} AND bsm.VIN LIKE CONCAT('%',#{VIN},'%') AND bsm.LICENSE_NO LIKE CONCAT('%',#{LICENSE_NO},'%') AND bsm.CUSTOMER_NAME LIKE CONCAT('%',#{CUSTOMER_NAME},'%') AND bsm.ORDER_STATUS =#{ORDER_STATUS}
AND bsm.CREATED_AT between #{CREATED_AT_START} and #{CREATED_AT_END}
AND bsm.CLOSED_AT between #{CLOSED_AT_START} and #{CLOSED_AT_END}
LIMIT #{startRow},#{rowSize}

select count(*) from (SELECT ttes.PRODUCT_SALES_ID, ttes.DEALER_CODE AS DEALER_CODE_TTES, TEDC.DEALER_CODE AS DEALER_CODE_TEDC, tmd.DEALER_CODE, tmd.DEALER_NAME, tmd.DEALER_SHORTNAME, tmd.COMPANY_ID, tmd.DEALER_ID, tmd.MARKETING, tmd.STATUS, tmd.DEALER_LEVEL, tmd.ADDRESS, tmd.DEALER_TYPE, ttes.PRODUCT_ID, ORG2.ORG_NAME AS BIG_ORG, ORG2.ORG_ID AS BIG_ORG_ID, ORG1.ORG_NAME AS SMALL_ORG, ORG1.ORG_ID AS SMALL_ORG_ID, ( CASE WHEN ttes.ORDER_STATUS = 55011001 THEN '已保存' WHEN ttes.ORDER_STATUS = 55011002 THEN '已结案' WHEN ttes.ORDER_STATUS = 55011003 THEN '扣款失败' WHEN ttes.ORDER_STATUS = 55011004 THEN '已作废' ELSE '' END ) ORDER_STATUS, ttes.PRODUCT_SALES_ORDER, ttes.SAP_SALES_ORDER, ttes.PRODUCT_NO, ttes.PRODUCT_NAME, toed.PRODUCT_DESCRIBTION, ( CASE WHEN toed.PRODUCT_DATE = 12 THEN '12个月' WHEN toed.PRODUCT_DATE = 24 THEN '24个月' WHEN toed.PRODUCT_DATE = 36 THEN '36个月' ELSE '' END ) PRODUCT_DATE, ttes.CUSTOMER_NAME, ttes.CUSTOMER_CONTACT, ttes.VIN, (CASE WHEN 1=1 THEN DATE_FORMAT(ttes.BILLING_AT,'%Y-%m-%d %H:%m:%s') ELSE '' END ) BILLING_AT, ttes.LICENSE_NO, ttes.OWNER_NO, ttes.OWNER_NAME, ttes.OWNER_PHONE, ttes.OWNER_MOBILE, ttes.BRAND_NAME, ttes.BRAND_CODE, ttes.SERIES_NAME, ttes.SERIES_CODE, ttes.MODEL_NAME, ttes.MODEL_CODE, ttes.APACKAGE, ttes.YEAR_MODEL, (CASE WHEN 1=1 THEN DATE_FORMAT(ttes.TAKE_EFFECT_START,'%Y-%m-%d %H:%m:%s') ELSE '' END ) TAKE_EFFECT_START, (CASE WHEN 1=1 THEN DATE_FORMAT(ttes.TAKE_EFFECT_END,'%Y-%m-%d %H:%m:%s') ELSE '' END ) TAKE_EFFECT_END, ctu.USER_NAME CREATE_NAME, (CASE WHEN 1=1 THEN DATE_FORMAT(ttes.CREATED_AT,'%Y-%m-%d %H:%m:%s') ELSE '' END ) CREATED_AT, ttes.PURCHASE_NUMBER, ttes.TERMINAL_NON_SALES_PRICE, ttes.TERMINAL_SALES_PRICE, ttes.ACTUAL_NON_SALES_PRICE, ttes.TOTAL_AMOUNT, ltu.USER_NAME CLOSED_NAME, (CASE WHEN 1=1 THEN DATE_FORMAT(ttes.CLOSED_AT,'%Y-%m-%d %H:%m:%s') ELSE '' END ) CLOSED_AT, itu.USER_NAME INVALID_NAME, (CASE WHEN 1=1 THEN DATE_FORMAT(ttes.INVALID_AT,'%Y-%m-%d %H:%m:%s') ELSE '' END ) INVALID_AT FROM tt_opi_extended_sales ttes LEFT JOIN ti_opi_extended_dcs toed ON ttes.PRODUCT_ID = toed.PRODUCT_ID LEFT JOIN tm_user ctu ON ttes.CREATED_BY = ctu.USER_ID LEFT JOIN tm_user ltu ON ttes.CLOSED_BY = ltu.USER_ID LEFT JOIN tm_user itu ON ttes.INVALID_BY = itu.USER_ID LEFT JOIN TM_ENTITY_DEALER_CHANGE TEDC ON ttes.DEALER_CODE = TEDC.ENTITY_CODE LEFT JOIN tm_dealer tmd ON TEDC.DEALER_CODE = REPLACE (tmd.DEALER_CODE, 'A', '') LEFT JOIN tm_dealer_org_relation tdor ON tdor.DEALER_ID = tmd.DEALER_ID LEFT JOIN tm_org ORG1 ON ORG1.ORG_ID = tdor.ORG_ID LEFT JOIN tm_org ORG2 ON ORG2.ORG_ID = ORG1.PARENT_ORG_ID) bsm where 1=1 and bsm.ORDER_STATUS= 55011002 AND bsm.BIG_ORG_ID=#{BIG_ORG_ID} AND bsm.SMALL_ORG_ID=#{SMALL_ORG_ID}
AND bsm.DEALER_CODE in
#{item}
AND bsm.DEALER_NAME LIKE CONCAT('%',#{DEALER_NAME},'%')
AND bsm.PRODUCT_DATE =#{PRODUCT_DATE} AND bsm.PRODUCT_SALES_ORDER LIKE CONCAT('%',#{PRODUCT_SALES_ORDER},'%') AND bsm.PRODUCT_NO LIKE CONCAT('%',#{PRODUCT_NO},'%') AND bsm.PRODUCT_NAME LIKE CONCAT('%',#{PRODUCT_NAME},'%')
AND bsm.BRAND_CODE =#{BRAND_CODE} AND bsm.SERIES_CODE =#{SERIES_CODE} AND bsm.MODEL_CODE =#{MODEL_CODE} AND bsm.VIN LIKE CONCAT('%',#{VIN},'%') AND bsm.LICENSE_NO LIKE CONCAT('%',#{LICENSE_NO},'%') AND bsm.CUSTOMER_NAME LIKE CONCAT('%',#{CUSTOMER_NAME},'%') AND bsm.ORDER_STATUS =#{ORDER_STATUS}
AND bsm.CREATED_AT between #{CREATED_AT_START} and #{CREATED_AT_END}
AND bsm.CLOSED_AT between #{CLOSED_AT_START} and #{CLOSED_AT_END}

