mapper.select:
select  ${distinct}

    ${resultColumn}




from sys_rescue_user





        ${order}


        inner join
        (
        SELECT id as primary_key FROM sys_rescue_user



        ${order}

            LIMIT #{startRow},#{rowSize} 
            LIMIT #{rowSize}

         ) as tmp
         on tmp.primary_key=sys_rescue_user.id

select 

from sys_rescue_user

     and sys_rescue_user.create_time = #{createTime}
     and sys_rescue_user.status = #{status}
     and sys_rescue_user.dept_id = #{deptId}
     and sys_rescue_user.phone = #{phone}
     and sys_rescue_user.age = #{age}
     and sys_rescue_user.sex = #{sex}
     and sys_rescue_user.password = #{password}
     and sys_rescue_user.name = #{name}
     and sys_rescue_user.login_name = #{loginName}
     and sys_rescue_user.id = #{id}

mapper.update:
update sys_rescue_user set
    login_name=#{loginName}
    ,name=#{name}
    ,password=#{password}
    ,sex=#{sex}
    ,age=#{age}
    ,phone=#{phone}
    ,dept_id=#{deptId}
    ,status=#{status}
    ,create_time=#{createTime}
where id=#{id}

mapper.select:
SELECT tdb.DEALER_CODE,te.GENDER,te.EMPLOYEE_NO,tu.PASSWORD,te.EMPLOYEE_NAME,te.MOBILE, tdb.ASC_SHORTNAME AS DEALER_SHORTNAME, tdb.ASC_NAME AS DEALER_NAME, tu.USER_ID,tu.USER_CODE,tu.USER_NAME,org.ORG_CODE,org.ORG_NAME,org.ORGDEPT_ID FROM tm_employee te INNER JOIN tm_user tu ON te.EMPLOYEE_NO = tu.EMPLOYEE_NO and te.DEALER_CODE = tu.DEALER_CODE LEFT JOIN TM_ASC_BASICINFO tdb ON tdb.DEALER_CODE=te.DEALER_CODE left join TM_ORGANIZATION org on org.ORG_CODE=tu.ORG_CODE and tdb.DEALER_CODE = org.DEALER_CODE WHERE te.IS_VALID='12781001' AND ( tu.USER_STATUS='12101001' or tu.USER_STATUS='12781001')
AND tdb.DEALER_CODE = #{dealerCode}
AND te.EMPLOYEE_NO = #{userCode}

SELECT	EMPLOYEE_NO,DEALER_CODE,USER_CODE,TARGET_SYSTEM,TARGET_USER,TARGET_USER_NAME
FROM TT_LOGIN_USER_MAP
WHERE 1=1

 	AND DEALER_CODE = #{dealerCode}


 	AND EMPLOYEE_NO  = #{userCode}

select  ${distinct}

    ${resultColumn}

		*


from tm_user tmu





        ${order}


        inner join
        (
        SELECT USER_ID,DEALER_CODE FROM tm_user



        ${order}

            LIMIT #{startRow},#{rowSize} 
            LIMIT #{rowSize}

         ) as tmp
         on tmp.USER_ID=tmu.USER_ID and tmp.DEALER_CODE = tmu.DEALER_CODE

        select  ${distinct}

            ${resultColumn}

        		ACNT as EMPLOYEE_NO,
  				NAME as USER_NAME,
  				LASTSIGNIN_TIME as LAST_LOG_TIME,
  				tmu.CREATE_DATE as CREATED_AT,
				tmc.COMPANY_CODE as DEALER_CODE


        from tc_user tmu





                ${order}


                inner join
                (
                SELECT USER_ID,COMPANY_ID FROM tc_user



                ${order}

                    LIMIT #{startRow},#{rowSize} 
                    LIMIT #{rowSize}

                 ) as tmp
                 on tmp.USER_ID=tmu.USER_ID and tmp.COMPANY_ID = tmu.COMPANY_ID
                 LEFT JOIN tm_company tmc ON tmu.COMPANY_ID = tmc.COMPANY_ID
